import React from 'react';
import Slider from 'react-slick'; // Correct import statement for react-slick

import img1 from '../../img/ico-destinonegocio-como-abrir-um-posto-de-gasolina-istock-getty-images1.jpg';
import img2 from '../../img/post_thumbnail-db20998d8244ab4d7fbda92f37d58f34-683x400.jpeg';
import img3 from '../../img/posto.jpg';

const ImageSlider = () => {
  const settings = {
    dots: true,
    infinite: true,
    speed: 500,
    slidesToShow: 1,
    slidesToScroll: 1,
    autoplay: true,
    autoplaySpeed: 3000,
  };

  const images = [img1, img2, img3];

  return (
    <Slider {...settings}>
      {images.map((img) => (
        <div key={img}> {/* Use img as a unique key */}
          <img src={img} alt="Slide" style={{ width: '100%', height: 'auto' }} />
        </div>
      ))}
    </Slider>
  );
};

export default ImageSlider;
